Use test;

DROP TABLE IF EXISTS Book;

CREATE TABLE `Book` (
  `ID` INT NOT NULL AUTO_INCREMENT,
  `Title` VARCHAR(100) NOT NULL,
  `Description` VARCHAR(255) NOT NULL,
  `Author` VARCHAR(100) NOT NULL,
  `ISBN` VARCHAR(20) NOT NULL,
  `PrintYear` INT NOT NULL,
  `ReadAlready` BIT(1) NOT NULL,
  PRIMARY KEY (`ID`))
  ENGINE=InnoDB
  COLLATE='utf8_general_ci';

INSERT INTO `Book` (`Title`,`Description`,`Author`,`ISBN`,`PrintYear`, `ReadAlready`)
    VALUES ("Изучаем Java", "Изучаем Java — это не просто книга. Она не только научит вас теории языка Java и объектно-ориентированного программирования, она сделает вас программистом.", 
    "Берт Бейтс, Кэти Сьерра", "978-5-699-54574-2", "2015", 0),
      ("Философия Java", "Книга, выдержавшая в оригинале не одно переиздание, за глубокое и поистине философское изложение тонкостей языка Java считается одним из лучших пособий для программистов.",
      "Брюс Эккель", "978-5-496-01127-3", "2017", 0),
      ("Java 8. Руководство для начинающих", "Настоящее, 6-е издание бестселлера Герберта Шилдта Java 8: руководство для начинающих, позволит новичкам сразу же приступить к программированию на языке Java.",
      "Герберт Шилдт", "978-5-8459-1955-7", "2015", 0),
      ("Программирование на Java для начинающих", "Полный спектр сведений о языке Java с примерами и разбором задач от автора учебников-бестселлеров по языкам программирования Алексея Васильева.",
      "Алексей Васильев", "978-5-699-89475-8", "2017", 0),
      ("Java 8. Карманный справочник", "Эта небольшая книга «Java 8: карманный справочник», включающая в себя описание новых возможностей Java, до Java SE 8 включительно, будет вашим идеальным спутником.",
      "Патрисия Лигуори, Роберт Лигуори", "978-5-8459-2050-8", "2016", 0),
      ("Структуры данных и алгоритмы в Java", "Алгоритмы - это основа программирования, определяющая, каким образом разрабатываемое программное обеспечение будет использовать структуры данных.",
      "Роберт Лафоре", "978-5-496-00740-5", "2015", 0),
      ("Java SE 8. Базовый курс", "Эта книга является полным, хотя и кратким справочником по версии Java SE 8. Она написана Кеем С. Хорстманном, автором книги Java SE 8.",
      "Кей Хорстманн", "978-5-8459-2004-1", "2015", 0),
      ("Алгоритмы на Java", "Книга Седжвика и Уэйна «АЛГОРИТМЫ на Java» является классическим справочным руководством в котором содержится необходимый объем знаний для программиста в области алгоритмов, накопленных за последние несколько десятилетий.",
      "Роберт Седжвик, Кевин Уэйн", "978-5-8459-2049-2", "2016", 0),
      ("Java. Библиотека профессионала. Том 2. Расширенные средства программирования", "Полностью обновленное для версии Java SE 7 издание представляет собой солидное справочное руководство по языку программирования Java для тех, кто разрабатывает настоящие проекты на платформе Java.",
      "Гари Корнелл, Кей Хорстманн", "978-5-8459-2031-7", "2015", 0),
      ("Java EE 7. Основы", "Книга написана одним из ведущих разработчиков проекта Java EE, и каждая глава в ней посвящена рассмотрению одной из ключевых спецификаций платформы, включая WebSockets, Batch Processing, RESTful Web Services и Java Message Service.",
      "Арун Гупта", "978-5-8459-1896-3", "2014", 0),
      ("Java. Библиотека профессионала. Том 1. Основы", "В этом надежном и полезном практическом руководстве описаны важные языковые средства и библиотеки, проиллюстрированные тщательно подобранными и проверенными примерами из практики программирования на Java.",
      "Гари Корнелл, Кей Хорстманн", "978-5-8459-1958-8", "2016", 0),
      ("Программирование на Java", "Комплексное руководство по освоению языка программирования Java с многочисленными подробными и реалистичными примерами.",
      "Даниэль Леук, Патрик Нимейер", "978-5-699-71504-6", "2014", 0),
      ("Java SE 8. Вводный курс", "В своей книге Кей С. Хорстманн, предлагает вниманию читателей наиболее ценные из новых языковых средств в версии Java 8, а также рассматривает те средства, которые были внедрены в версии Java 7, но не удостоились должного внимания программистов.",
      "Кей Хорстманн", "978-5-8459-1900-7", "2014", 0),
      ("Самоучитель Java 7", "Книга содержит все необходимое для самостоятельного быстрого и успешного освоения программирования на языке Java 7.",
      "Дирк Льюис, П. Мюллер", "978-5-9775-0815-5", "2017", 0),
      ("Java. Эффективное программирование", "Всесторонне описывая приемы, которыми пользуются эксперты, создававшие платформу Java, эта книга показывает, что следует делать, а чего делать не следует для получения понятного, надежного и эффективного программного кода.",
      "Джошуа Блох", "978-5-85582-347-9", "2014", 0),
      ("Spring в действии", "В книге описана последняя версия Spring 3, который несет в себе новые мощные особенности, такие как язык выражений SpEL, новые аннотации для работы с контейнером IоС и поддержка архитектуры REST.",
      "Крейг Уоллс", "978-5-97060-171-6", "2015", 0),
      ("Java 8 для чайников", "Мир Java. Познакомьтесь с усовершенствованной платформой Java и новыми средствами версии Java 8, включая библиотеки параллельного программирования и многое другое.",
      "Барри Берд", "978-5-8459-1928-1", "2015", 0),
      ("JavaFX 2.0. Разработка RIA-приложений", "Книга посвящена разработке RIA-приложений (Rich Internet Applications) с использованием технологии JavaFX 2.0.",
      "Берт Бейтс, Кэти Сьерра", "978-5-9775-0820-9", "2012", 0),
      ("Spring 4 для профессионалов", "Книга представляет собой многолетний бестселлер, который обновлен с целью отражения функциональных средств, предлагаемых последней версией платформы Spring Framework 4.",
      "Крис Шефер, Кларенс Хо, Роб Харроп", "978-5-8459-1992-2", "2017", 0),
      ("EJB 3 в действии", "Фреймворк EJB 3 предоставляет стандартный способ оформления прикладной логики в виде управляемых модулей, которые выполняются на стороне сервера. Книга знакомит читателя с EJB на многочисленных примерах кода, сценариях из реальной жизни и иллюстрациях.",
      "Дебу Панда, Реза Рахман, Райан Купрак, Майкл Ремижан", "978-5-97060-135-8", "2015", 0),
      ("Язык программирования Java SE 8. Подробное описание", "Книга Язык программирования Java SE 8. Подробное описание является каноническим описанием языка программирования Java. Один из авторов книги Джеймс Гослинг (James Gosling) является отцом языка Java и известен как основной разработчик этого языка.",
      "Джеймс Гослинг, Билл Джой, Гай Л. Стил, Гилад Брача, Алекс Бакли", "978-5-8459-1875-8", "2015", 0),
      ("Java EE 6 и сервер приложений GlassFish 3", "Книга представляет собой практическое руководство. Все рассмотренные основные интерфейсы Java ЕЕ 6 и подробная информация о сервере GlassFish 3 подкреплены практическими примерами их использования.",
      "Дэвид Хеффельфингер", "978-5-94074-902-8", "2013", 0);
